workflow-item.script:
//creating the virtual machine config spec
var resourceAlloc = new VcResourceAllocationInfo();
resourceAlloc.reservation= newMemoryReservation;
var configSpec = new VcVirtualMachineConfigSpec();
configSpec.memoryAllocation = resourceAlloc;

System.log("Reconfiguring the memory reservation limit for the virtual machine : "+vm);
//reconfiguring the vm config spec to update the cpu reservation limit
task = vm.reconfigVM_Task(configSpec);

for each(var v in hostSystem.vm){
	if(v.name == vm.name){
		System.log("Selected Virtual Machine is available");
		System.log("vm.state: "+vm.state);
		validateReservation(vm)
		break;
	}
}

function validateReservation(vm){
	//obtaining the current memory reservation limit
	var memoryReservationLimit = vm.config.memoryAllocation.limit;

	if(memoryReservationLimit<newMemoryReservation && memoryReservationLimit!=-1){
		throw "Provided memory reservation value :"+newMemoryReservation+" is more than memory reservation limit :"+memoryReservationLimit+" of the virtual machine";
	}
}

System.log("Reconfiguration of the virtual machine is successful");
System.log("Successfully updated the memory reservation limit of virtual machine ["+vm.name + "]");
updatedVM=vm;
