workflow-item.script:
var vSwitches = host.configManager.networkSystem.networkInfo.vswitch
for each (vSwitch in vSwitches) {
	spec = vSwitch.spec
	spec.policy.security.forgedTransmits = false;

	var myVcHostNetworkConfigResult = host.configManager.networkSystem.updateVirtualSwitch(vSwitch.name, spec);
}
